                'description' => '<div class="et_pb_section et_pb_section_1 pagination-row et_pb_with_background et_section_regular" style="box-sizing: border-box; margin: 0px; border: 0px; outline: 0px; font-size: 14px; text-size-adjust: 100%; vertical-align: baseline; background-image: initial; background-position: center center; background-size: cover; background-repeat: no-repeat; background-attachment: initial; background-origin: initial; background-clip: initial; background-color: rgba(249, 248, 246, 0.48) !important; position: relative; color: rgb(102, 102, 102); font-family: &quot;Open Sans&quot;, Arial, sans-serif; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 500; letter-spacing: normal; orphans: 2; text-align: left; text-indent: 0px; text-transform: none; widows: 2; word-spacing: 0px; -webkit-text-stroke-width: 0px; white-space: normal; text-decoration-thickness: initial; text-decoration-style: initial; text-decoration-color: initial;"><div class="et_pb_row et_pb_row_0 footer-section" style="box-sizing: border-box; margin-right: auto !important; margin-bottom: auto; margin-left: auto !important; padding: 0px; border: 0px; outline: 0px; font-size: 14px; text-size-adjust: 100%; vertical-align: baseline; background: center center / cover no-repeat transparent; max-width: 1080px; position: relative;"><div class="et_pb_column et_pb_column_4_4 et_pb_column_0  et_pb_css_mix_blend_mode_passthrough et-last-child" style="box-sizing: border-box; margin-top: 0px; margin-right: 0px !important; margin-bottom: 0px; margin-left: 0px; padding: 0px; border: 0px; outline: 0px; font-size: 14px; text-size-adjust: 100%; vertical-align: baseline; background: center center / cover no-repeat transparent; float: left; position: relative; z-index: 2; min-height: 1px; mix-blend-mode: unset !important;"><div class="et_pb_module et_pb_post_title et_pb_post_title_0 et_pb_bg_layout_light  et_pb_text_align_left" style="box-sizing: border-box;